# run_hmmer.pyimport subprocessimport osimport loggingdef filter_hmmer_output_inplace(tblout_file):    """Filter HMMER tblout output to remove duplicate hit IDs."""    temp_file = f"{tblout_file}.tmp"    seen_ids = set()    with open(tblout_file, "r") as infile, open(temp_file, "w") as outfile:        for line in infile:            if line.startswith("#"):                outfile.write(line)  # Preserve comment lines                continue                        fields = line.split(maxsplit=20)            if len(fields) < 21:  # Skip malformed lines                continue                        hit_id = fields[0]            if hit_id in seen_ids:                continue            seen_ids.add(hit_id)            try:                score = float(fields[5])                bias = float(fields[6])            except ValueError:                continue                        # Apply bias filtering criteria            if bias < score * 0.1:                outfile.write(line)    os.replace(temp_file, tblout_file)def filter_by_bitscore(tblout_file, min_bitscore=50):    """Filter HMMER tblout output by bitscore."""    temp_file = f"{tblout_file}.filtered"    with open(tblout_file, "r") as infile, open(temp_file, "w") as outfile:        for line in infile:            if line.startswith("#"):                outfile.write(line)  # Keep header lines                continue                        fields = line.split()            if len(fields) >= 6:  # Ensure enough fields for bitscore                try:                    bitscore = float(fields[5])                    if bitscore >= min_bitscore:                        outfile.write(line)                except ValueError:                    continue    os.replace(temp_file, tblout_file)def run_hmmer(proteins, polymer, outdir, evalue, cores, bitscore):    """Perform HMMER search for specific polymers or multiple polymers with multiple .hmm files."""    package_dir = os.path.dirname(os.path.dirname(os.path.abspath(__file__)))    hmm_dir = os.path.join(package_dir, "data", "polymer_hmms")    polymers = polymer.split(",") if polymer != "all" else "all"    hmm_files = []    if polymers == "all":        hmm_files = [os.path.join(hmm_dir, f) for f in os.listdir(hmm_dir) if f.endswith(".hmm")]    else:        polymer_to_hmm = {            "P3HP": ["P3HP.hmm"], "PBAT": ["PBAT.hmm"],            "PBS": ["PBS.hmm"], "PBSA": ["PBSA.hmm"], "PCL": ["PCL.hmm"],            "PEA": ["PEA.hmm"], "PET": ["PET.hmm"], "PHA": ["PHA.hmm"],            "PHB": ["PHB.hmm"], "PLA": ["PLA.hmm"],            "PHBV": ["PHBV.hmm"]        }        for poly in polymers:            poly = poly.strip().upper()            if poly in polymer_to_hmm:                for hmm in polymer_to_hmm[poly]:                    hmm_path = os.path.join(hmm_dir, hmm)                    if os.path.exists(hmm_path):                        hmm_files.append(hmm_path)    os.makedirs(outdir, exist_ok=True)    base_name = os.path.basename(proteins).split('_')[0]    all_output_files = []    for hmm_file in hmm_files:        hmm_base = os.path.splitext(os.path.basename(hmm_file))[0]        tblout_file = os.path.join(outdir, f"{base_name}_{hmm_base}_HMMER.tblout")        log_file = os.path.join(outdir, "hmmer.log")        cmd = [            "hmmsearch",            "-E", str(evalue),            "--incE", str(evalue),            "--cpu", str(cores),            "--noali",            "--tblout", tblout_file,            hmm_file,            proteins        ]        if os.path.exists(log_file):            os.remove(log_file)                try:            with open(log_file, "a") as log:                log.write(f"üß™Running command: {' '.join(cmd)}\n")                log.write(f"üß™Using bitscore filter: {bitscore}\n")                subprocess.check_call(cmd, stdout=log, stderr=log)            if os.path.exists(tblout_file):                filter_hmmer_output_inplace(tblout_file)                filter_by_bitscore(tblout_file, min_bitscore=bitscore)                all_output_files.append(tblout_file)        except subprocess.CalledProcessError as e:            with open(log_file, "a") as log:                log.write(f"‚ùåERROR: HMMER command failed: {e}\n")    print(f"‚úÖHMMER completed successfully for polymer(s): {polymer}.")    return all_output_files