import sqlite3import osdef database_fetch(plastic_type, info_type, output_dir):    # Connect to the SQLite database    database_path = os.path.join(os.getcwd(), "PlasticEnzymes.db")  # Replace "your_database.db" with your database name    conn = sqlite3.connect(database_path)    cursor = conn.cursor()    # Depending on the info_type, select the appropriate sequence    if info_type == 'aa':        column = 'AA-Sequence'    elif info_type == 'nn':        column = 'NN-Sequence'    else:        print(f"Invalid info type: {info_type}")        return    # SQL query    query = f"""    SELECT Main.Genus, Main.Species, Main.Strain, Main.Enzyme, Sequences.'{column}'    FROM Main    JOIN Sequences ON Main.Sequences_id = Sequences.Seq_Pk    JOIN Plastic ON Main.Plastic_id = Plastic.Plastic_id    WHERE Plastic.Plastic_type = ?;    """    # Execute the query    cursor.execute(query, (plastic_type,))    # Fetch all rows from the executed SQL query    rows = cursor.fetchall()    # Close the connection    conn.close()    # Save the fetched data in FASTA format    output_file_path = os.path.join(output_dir, f"{plastic_type}_{info_type}.fasta")    with open(output_file_path, 'w') as output_file:        for row in rows:            header = ">{}_{}_{}_{}".format(row[0], row[1], row[2], row[3]).replace(' ', '_')            output_file.write(f"{header}\n{row[4]}\n")